mapper.update:
update 

set ROLE_NAME = #{ROLE_NAME},
	INTRODUCTION = #{INTRODUCTION},
	ROLE_TYPE = #{ROLE_TYPE}
where ROLE_ID = #{ROLE_ID}

mapper.delete:
	delete from 
	 
 	where 
	
		
	 	
	 		FIND_IN_SET(ROLE_ID, getChild(#{ROLE_ID},1));
	 	
	 	
			ROLE_ID in ( 
				select ROLE_ID  from SYS_ROLE START WITH ROLE_ID =#{ROLE_ID} CONNECT BY PRIOR ROLE_ID=PARENT_ID
		 	)

	delete from ROLE_MF_RELA 
 	where 
	
		
	 	
	 		FIND_IN_SET(ROLE_ID, getChild(#{ROLE_ID},1));
	 	
	 	
			ROLE_ID in ( 
				select ROLE_ID  from SYS_ROLE START WITH ROLE_ID =#{ROLE_ID} CONNECT BY PRIOR ROLE_ID=PARENT_ID
		 	)

	delete from SYS_ROLE_TABLE_RELATION 
 	where 
	
		
	 	
	 		FIND_IN_SET(ROLE_ID, getChild(#{ROLE_ID},1);
	 	
	 	
			ROLE_ID in ( 
				select ROLE_ID  from SYS_ROLE START WITH ROLE_ID =#{ROLE_ID} CONNECT BY PRIOR ROLE_ID=PARENT_ID
		 	)

	delete from USER_ROLE_RELA 
 	where 
	
		
	 	
	 		FIND_IN_SET(ROLE_ID, getChild(#{ROLE_ID},1));
	 	
	 	
			ROLE_ID in ( 
				select ROLE_ID  from SYS_ROLE START WITH ROLE_ID =#{ROLE_ID} CONNECT BY PRIOR ROLE_ID=PARENT_ID
		 	)

mapper.insert:
insert into ROLE_MF_RELA
(
	ID,
	ROLE_ID,
	MF_ID
) values (
	#{ID},
	#{ROLE_ID},
	#{MF_ID}
)
